import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import JobApplicationList from "./components/JobApplicationList";
import AddJobApplication from "./components/AddJobApplication";
import EditJobApplication from "./components/EditJobStatus";

const App: React.FC = () => {
  const jobApplication = {
    id: 1,
    company: "ABC Corp",
    position: "Developer",
    status: "applied",
    applicationDate: "2025-01-01",
  };

  return (
    <Router>
      <div className="App">
        <h1>Job Application Tracker</h1>
        <Routes>
          <Route path="/" element={<JobApplicationList />} />
          <Route path="/add" element={<AddJobApplication />} />
          <Route
            path="/edit"
            element={
              <EditJobApplication />  // EditJobApplication now gets the jobApplication from location.state
            }
          />
        </Routes>
        {/* Link to edit page with job application passed in state */}
        <Link
          to={{
            pathname: "/edit",
            state: { jobApplication },  // Passing jobApplication data
          }}
        >
          Edit Job Application
        </Link>
      </div>
    </Router>
  );
};

export default App;
