import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { AppDispatch } from "../redux/store";
import { fetchJobApplications } from "../redux/jobApplicationSlice";
import axios from "axios";
import { useLocation } from "react-router-dom";

interface JobApplication {
  id: number;
  company: string;
  position: string;
  status: string;
  applicationDate: string;
}

const EditJobApplication: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const location = useLocation();
  const { jobApplication }: { jobApplication: JobApplication } = location.state || {}; // Extract jobApplication from location.state

  const [updatedJob, setUpdatedJob] = useState<JobApplication>(jobApplication);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (jobApplication) {
      setUpdatedJob(jobApplication);
    }
  }, [jobApplication]);

  const handleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setUpdatedJob({ ...updatedJob, status: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      await axios.put(
        `https://localhost:8484/api/JobApplication/${updatedJob.id}`,
        { status: updatedJob.status },
        { headers: { "Content-Type": "application/json" } }
      );

      dispatch(fetchJobApplications());
    } catch (err) {
      setError("Failed to update status. Please try again.");
      console.error("Error updating status:", err);
    } finally {
      setLoading(false);
    }
  };

  if (!jobApplication) {
    return <p>No job data available!</p>;
  }

  return (
    <form onSubmit={handleSubmit} className="p-4 border rounded shadow-md bg-white">
      {error && <p className="text-red-500 mb-2">{error}</p>}
      <div>
        <label className="block font-bold">Status</label>
        <select
          name="status"
          value={updatedJob.status}
          onChange={handleChange}
          className="w-full p-2 border rounded"
        >
          <option value="applied">Applied</option>
          <option value="waiting">Waiting</option>
          <option value="rejected">Rejected</option>
        </select>
      </div>
      <button
        type="submit"
        className="mt-4 bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
        disabled={loading}
      >
        {loading ? "Updating..." : "Update Status"}
      </button>
    </form>
  );
};

export default EditJobApplication;
